// ver no console do browser __proto__
// ex: "teste".__proto__ ou 12.0.__proto__ ou true.__proto__ ou [].__proto__ ou {}.__proto__

const leo = "Leo Banana"

const validat = leo.length <= 3 ? "Sim" : "não"
console.log(validat)

// ver tamanho dado.length
const name = "jubiscleudo"
console.log(name.length)

// number não tem essa opção, transformar para string
const number = 123
console.log(String(number).length)